from collections import defaultdict

# Dictionary to store counts
discipline_counts = defaultdict(int)
sport_counts = defaultdict(int)

# Loop through and count
for event in OLYMPIC_EVENTS:
    discipline_counts[event["discipline"]] += 1

# Print the final tally
print(f"{'DISCIPLINE':<20} | {'EVENT COUNT':<10}")
print("-" * 35)
for disc, count in sorted(discipline_counts.items()):
    print(f"{disc:<20} | {count:<10}")

# Quick verify: Total should be 116
print("-" * 35)
print(f"{'TOTAL':<20} | {sum(discipline_counts.values()):<10}")

# Loop through and count
for event in OLYMPIC_EVENTS:
    sport_counts[event["sport"]] += 1

# Print the final tally
print("\n\n")
print(f"{'SPORT':<20} | {'EVENT COUNT':<10}")
print("-" * 35)
for disc, count in sorted(sport_counts.items()):
    print(f"{disc:<20} | {count:<10}")

# Quick verify: Total should be 116
print("-" * 35)
print(f"{'TOTAL':<20} | {sum(discipline_counts.values()):<10}")